import React, { useState } from 'react';
import { Search } from 'lucide-react';

const ScoreManagement = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedStudent, setSelectedStudent] = useState(null);

  // This would come from an API in a real application
  const [students] = useState([
    {
      id: 1,
      name: "John Doe",
      class: "5A",
      subjects: [
        { id: 1, name: "Mathematics", score: null },
        { id: 2, name: "Science", score: null },
        { id: 3, name: "English", score: null }
      ]
    },
    // Add more students
  ]);

  const handleScoreUpdate = (studentId, subjectId, newScore) => {
    // Validate score
    if (newScore < 0 || newScore > 100) {
      alert('Score must be between 0 and 100');
      return;
    }

    // Update score logic would go here
    console.log(`Updating score for student ${studentId}, subject ${subjectId}: ${newScore}`);
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow">
      <h2 className="text-2xl font-bold mb-6">Score Management</h2>

      {/* Search Bar */}
      <div className="flex items-center gap-2 mb-6">
        <Search className="text-gray-400" size={20} />
        <input
          type="text"
          placeholder="Search students..."
          className="w-full p-2 border rounded"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {/* Student List */}
      <div className="grid grid-cols-1 gap-4">
        {students
          .filter(student => 
            student.name.toLowerCase().includes(searchQuery.toLowerCase())
          )
          .map(student => (
            <div key={student.id} className="border rounded-lg p-4">
              <div className="flex justify-between items-center mb-4">
                <div>
                  <h3 className="text-lg font-semibold">{student.name}</h3>
                  <p className="text-gray-600">Class: {student.class}</p>
                </div>
                <button
                  onClick={() => setSelectedStudent(student)}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  Update Scores
                </button>
              </div>

              {selectedStudent?.id === student.id && (
                <div className="mt-4">
                  <table className="w-full">
                    <thead>
                      <tr className="bg-gray-50">
                        <th className="px-4 py-2 text-left">Subject</th>
                        <th className="px-4 py-2 text-left">Score</th>
                        <th className="px-4 py-2 text-left">Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {student.subjects.map(subject => (
                        <tr key={subject.id} className="border-t">
                          <td className="px-4 py-2">{subject.name}</td>
                          <td className="px-4 py-2">
                            {subject.score ?? 'Not graded'}
                          </td>
                          <td className="px-4 py-2">
                            <input
                              type="number"
                              min="0"
                              max="100"
                              className="w-20 p-1 border rounded"
                              placeholder="Score"
                              onChange={(e) => 
                                handleScoreUpdate(
                                  student.id, 
                                  subject.id, 
                                  parseInt(e.target.value)
                                )
                              }
                            />
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          ))}
      </div>
    </div>
  );
};

export default ScoreManagement;
