import React, { useState } from 'react';

const SeatingManagement = () => {
  const [classrooms, setClassrooms] = useState([
    { id: 1, name: "Class 5A" },
    { id: 2, name: "Class 5B" },
    { id: 3, name: "Class 5C" },
    { id: 4, name: "Class 5D" },
    { id: 5, name: "Class 5E" },
    { id: 6, name: "Extra Room" }
  ]);

  const [selectedClassroom, setSelectedClassroom] = useState(null);
  const [selectedSeat, setSelectedSeat] = useState(null);

  const handleSeatClick = (row, col) => {
    if (!selectedClassroom) return;

    setSelectedSeat({ row, col });
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6">Seating Management</h2>

      {/* Classroom Selection */}
      <div className="mb-6 flex gap-4">
        {classrooms.map((classroom) => (
          <button
            key={classroom.id}
            className={`px-4 py-2 rounded ${
              selectedClassroom?.id === classroom.id
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 hover:bg-gray-300'
            }`}
            onClick={() => setSelectedClassroom(classroom)}
          >
            {classroom.name}
          </button>
        ))}
      </div>

      {selectedClassroom && (
        <div className="flex gap-6">
          {/* Seating Grid */}
          <div className="flex-1">
            <h3 className="text-xl font-semibold mb-4">{selectedClassroom.name} - Seating Plan</h3>
            <div className="grid grid-cols-5 gap-2 bg-gray-100 p-4 rounded">
              {Array(25)
                .fill(null)
                .map((_, index) => {
                  const row = Math.floor(index / 5) + 1; // Adjusted to start from 1
                  const col = (index % 5) + 1; // Adjusted to start from 1

                  return (
                    <div
                      key={index}
                      onClick={() => handleSeatClick(row - 1, col - 1)} // Adjusted for zero-based indexing in the state
                      className={`
                        h-24 p-2 rounded cursor-pointer
                        bg-white hover:bg-gray-50
                        ${selectedSeat?.row === row - 1 && selectedSeat?.col === col - 1
                          ? 'ring-2 ring-blue-500'
                          : ''
                        }
                      `}
                    >
                      <div className="text-sm text-gray-700">{`${row},${col}`}</div>
                    </div>
                  );
                })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SeatingManagement;
